"use strict";(globalThis.webpackChunkmy_website=globalThis.webpackChunkmy_website||[]).push([[256],{7674:e=>{e.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"link","href":"/docs/intro","label":"Introduction","docId":"intro","unlisted":false},{"type":"category","label":"User Guide","collapsible":true,"collapsed":true,"items":[{"type":"link","href":"/docs/user-guide/toll-settings","label":"Toll Settings","docId":"user-guide/toll-settings","unlisted":false},{"type":"link","href":"/docs/user-guide/backup-account","label":"Backup Account","docId":"user-guide/backup-account","unlisted":false},{"type":"link","href":"/docs/user-guide/lock","label":"Lock","docId":"user-guide/lock","unlisted":false}],"href":"/docs/category/user-guide"},{"type":"category","label":"Validator","collapsible":true,"collapsed":true,"items":[{"type":"link","href":"/docs/validator/overview","label":"Liberdus Validator","docId":"validator/overview","unlisted":false},{"type":"link","href":"/docs/validator/staking","label":"Staking","docId":"validator/staking","unlisted":false},{"type":"link","href":"/docs/validator/rewards","label":"Rewards","docId":"validator/rewards","unlisted":false},{"type":"link","href":"/docs/validator/infrastructure-and-security","label":"Infrastructure & Security","docId":"validator/infrastructure-and-security","unlisted":false}],"href":"/docs/category/validator"},{"type":"link","href":"/docs/frequently-asked-questions","label":"Frequently Asked Questions","docId":"frequently-asked-questions","unlisted":false}]},"docs":{"frequently-asked-questions":{"id":"frequently-asked-questions","title":"Frequently Asked Questions","description":"Coming Soon","sidebar":"tutorialSidebar"},"intro":{"id":"intro","title":"Introduction","description":"Liberdus is a decentralized messaging and payment platform designed for privacy, scalability, and resilience. It combines secure communication with cryptocurrency transactions, allowing users to send messages and LIB tokens in a single operation.","sidebar":"tutorialSidebar"},"user-guide/backup-account":{"id":"user-guide/backup-account","title":"Backup Account","description":"Liberdus allows you to back up and restore your account data across devices and platforms. Backups are encrypted and portable, enabling secure migration without losing messages, keys, or settings.","sidebar":"tutorialSidebar"},"user-guide/lock":{"id":"user-guide/lock","title":"Lock","description":"This guide will walk you through the process of locking your Liberdus account with a password. Locking your account adds an extra layer of security, ensuring that only you can access it after signing out.","sidebar":"tutorialSidebar"},"user-guide/toll-settings":{"id":"user-guide/toll-settings","title":"Toll Settings","description":"The Toll Settings section controls how much another user must pay to send you a message or payment in Liberdus. This feature helps manage spam and ensures fair network participation.","sidebar":"tutorialSidebar"},"validator/infrastructure-and-security":{"id":"validator/infrastructure-and-security","title":"Infrastructure & Security","description":"Reliable infrastructure and secure operations are critical to running a Liberdus validator. This page covers hardware requirements, uptime expectations, slashing risks, and best practices for maintaining node security.","sidebar":"tutorialSidebar"},"validator/overview":{"id":"validator/overview","title":"Liberdus Validator","description":"The Liberdus Validator is a core component of the Liberdus network, responsible for securing consensus, validating transactions, and participating in the governance and reward system. This section covers its roles, requirements, lifecycle, risks, and operational guidelines.","sidebar":"tutorialSidebar"},"validator/rewards":{"id":"validator/rewards","title":"Rewards","description":"Active Liberdus validators earn LIB tokens as rewards for validating transactions and ensuring network uptime. Rewards are distributed periodically and are proportional to each validator\u2019s participation during active periods. This page explains how Liberdus validators earn and claim rewards for participating in network consensus.","sidebar":"tutorialSidebar"},"validator/staking":{"id":"validator/staking","title":"Staking","description":"Validators secure the Liberdus network by staking LIB tokens as collateral, ensuring honesty and commitment to network integrity. By participating in staking, validators maintain eligibility to validate transactions and earn rewards, while risking a portion of their staked tokens if they act maliciously or remain offline. This page explains how Liberdus validators join the network, maintain eligibility, and manage staked LIB tokens.","sidebar":"tutorialSidebar"}}}}')}}]);